import React from 'react';
import {makeStyles,Avatar, MenuList, MenuItem} from '@material-ui/core';
import theme from '../theme';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    small: {
        width: theme.spacing(3),
        height: theme.spacing(3),
    },
    medium: {
        width: theme.spacing(10),
        height: theme.spacing(10),
    },
    large: {
        width: theme.spacing(15),
        height: theme.spacing(15),
    },
    avatar: {
        margin: "auto",
        marginTop: "50px",

    },
    menuitem: {
        height: "60px",
        width: "100%",
        background: "#212121",
        color: "#eeeeee", 
        '&:hover': {
            background: "#424242",
            color: "#fafafa"
        },
    }
}));

function SideBar() {
    const classes = useStyles(theme);
    return (
      <div className={classes.root}>  
        <Avatar className={classes.avatar} src="https://ca.slack-edge.com/T010MBR22KU-U019QV28AD8-b9e116383a90-512" className={classes.large}></Avatar>
        <MenuList>
            <MenuItem className={classes.menuitem}>Computer Science Blog</MenuItem>
            <MenuItem className={classes.menuitem}>Data Science Blog</MenuItem>
            <MenuItem className={classes.menuitem}>Machine Learning Blog</MenuItem>
            <MenuItem className={classes.menuitem}>Other Blog</MenuItem>
        </MenuList>
      </div>
    );
  }

  export default SideBar;
